        <span id="chevron" onClick={() => setShow(true)}>
          <FaChevronDown
            id="chevron-icon-down"
            className="chevron-icon-down"
            onClick={handleClickArrow}
          />
        </span>
      </div>
      <p
        id="description"
        className="about-info-content"
        onClick={() => setShow(false)}
      >
        {props.text}
      </p>
    </div>
  );
}

export default AboutInfo;
