import React from "react";
import "../../styles/About.css";
// import aboutImg from "../../assets/background-about.png";

import { about } from "../../datas/about";
import { handleClick } from "../../components/Dropdown";
import { FaChevronDown } from "react-icons/fa";
// import { FaChevronUp } from "react-icons/fa";

function About() {
  return (
    <div className="locationWrapper">
      {about.map((item, itemIndex) => {
        return (
          <>
            <div className="descrWrapper">
              <div
                key={itemIndex}
                className="descr-header"
                id="descr-header"
                onClick={handleClick}
              >
                <span>{item.title}</span>
                <span id="chevron-descr">
                  <FaChevronDown
                    id="chevron-icon-down"
                    className="chevron-icon-down"
                  />
                </span>
              </div>
              <div id="content" className="descr-content">
                {item.text}
              </div>
            </div>
          </>
        );
      })}
    </div>
  );
}

export default About;
