import "../../styles/About.css";
import aboutImg from "../../assets/background-about.png";
import { FaChevronDown } from "react-icons/fa";

function About() {
  function handleClick(e) {
    const content = document.getElementById("fiabiliteContent");
    const icon = document.getElementById("chevron-icon-down");
    if (content.style.display === "none") {
      content.style.display = "flex";
      icon.classList.add("rotateIcon");
    } else {
      content.style.display = "none";
      icon.classList.remove("rotateIcon");
    }
  }

  return (
    <div className="aboutWrapper">
      <div className="aboutContainer">
        <div>
          <img src={aboutImg} alt="" className="aboutImg" />
        </div>
      </div>
      <div className="about">

        <div className="fiabiliteWrapper">
          <div className="fiabilite-header">
            <span>Fiabilite </span>
            <span id="chevron-fiabilite">
              <FaChevronDown
                id="chevron-icon-down"
                className="chevron-icon-down"
                onClick={handleClick}
              />
            </span>
          </div>
          <div id="fiabiliteContent" className="fiabilite-content">
            Les annonces postées sur Kasa garantissent une fiabilité totale. Les
            photos sont conformes aux logements, et toutes les informations sont
            régulièrement vérifiées par nos équipes.
          </div>
        </div>

        <h3 className="aboutFiabilite">Respect</h3>
        <h3 className="aboutFiabilite">Service</h3>
        <h3 className="aboutFiabilite">Responsabilite</h3>
      </div>
    </div>
  );
}

export default About;
