import Card from '../../components/Card'
function Location() {

  return (
      <div className="galleryContainer">
          {Data && Data.map((location, index) => (
              <Card
                  key={`${location.title}-${index}`}

                  title={location.title}
                  id= {location.id}

              />
          ))}

      </div>
  )
}

export default Location;

