import { useRef, useEffect } from 'react'
import { useCerousel } from '../utils/hooks/useCarousel'
import PropTypes from 'prop-types'


function Carousel({id, cover, pictures}) {
  const currentPicture = useRef(null)
  const { goToPreviousPicture, gotToNextPicture } = useCerousel(currentPicture, pictures)
  const counter = pictures.length

  let pictureCounter = 0
  useEffect(() => startCarousel())

  const startCarousel = () => {
    currentPicture.current.src = pictures[0]
}


const handleCarousel = picture => {
  currentPicture.current.src = pictures[picture - 1]
  animateCarousel(currentPicture)
}

const animateCarousel = () => {
  currentPicture.current.classList.add("fadeIn")
  setTimeout(()=> { currentPicture.current.classList.remove("fadeIn")}, 700)
}

goToPreviousPicture = () => {
  if(pictureCounter === 0) {
      handleCarousel(pictures.length)
      pictureCounter = pictures.length
  }

  handleCarousel(pictureCounter)
  pictureCounter--
}

gotToNextPicture = () => {
  // For DEBUGG : console.log(pictureCounter + " > " + pictures.length)
  if (pictureCounter === pictures.length-1) {
      startCarousel()
      pictureCounter = -1
  }

  currentPicture.current.src = pictures[pictureCounter + 1]
  pictureCounter++
}

return { goToPreviousPicture, gotToNextPicture}

  return (
      <div className="slideWrapper">
          <div className="slideContainer">  
              {counter === 1 ? (
                  <img src={cover} alt="Carrousel" />
              ) : ( 
                  <div>
                      <button onClick={goToPreviousPicture} className="btn-left"><i className="fas fa-angle-left"></i></button>
                      <img src={currentPicture} ref={currentPicture} alt="" className="slidePicture"/>
                      <button onClick={gotToNextPicture} className="btn-right"><i className="fas fa-angle-right"></i></button>
                  </div>
              )}              
          </div>        
      </div>
  )
}

Carousel.propTypes = {
  id: PropTypes.string.isRequired,
  cover: PropTypes.string.isRequired,
  pictures: PropTypes.array.isRequired
}

export default Carousel
