// import { handleClickArrow } from "../HandleClickArrow/handleClickArrow";

function AboutInfo(props) {
  const [clicked, setClicked] = useState(false);
  const toggle = (index) => {
    if (clicked === index) {
      return setClicked(null);
    }
    setClicked(index);
  };
